"use strict";
jQuery(function (r) {
    r(document).ready(function () {
        r(".js-social-share").length &&
            (r(".js-social-share .js-facebook-share").attr(
                "href",
                "https://facebook.com/sharer/sharer.php?u=" +
                    window.location.href
            ),
            r(".js-social-share .js-twitter-share").attr(
                "href",
                "https://twitter.com/share?url=" + window.location.href
            ),
            r(".js-social-share .js-linkedin-share").attr(
                "href",
                "https://www.linkedin.com/sharing/share-offsite/?url=" +
                    window.location.href
            ));
    });
});
